import { Component } from 'react'
import './App.css'
import { Square } from './Square'

var oldHref = "http://localhost:3000"

interface Cell {
  text: String;
  clazz: String;
}

interface WorkerState {
  workerId: Number;
  x: Number;
  y: Number;
}

interface PlayerState {
  playerId: Number;
  workerStates: Array<WorkerState>;
  godCardString: String;
}

interface Cells {
  cells: Array<Cell>,
  instructions: String,
  playerStates: Array<PlayerState>
  workerSelection: Number
  posSelection: Number
  turn: Number
  winner: Number
  canMove: boolean
  canBuild: boolean
}

interface Props {
}

class App extends Component<Props, Cells> {
  constructor(props: Props) {
    super(props);
    this.state = {
      cells: [ 
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"}, 
      ],
      instructions: "",
      posSelection: -1,
      workerSelection: -1,
      turn: 0,
      playerStates: [],
      winner: -1,
      canBuild: true,
      canMove: true,
    };
    this.clickHandler = this.clickHandler.bind(this);
    this.resetSelections = this.resetSelections.bind(this);
  }


  clickHandler(index: number){
    if(this.tileHasWorkerFromPlayer(index, this.state.turn.valueOf()) != -1) {
      this.setState({workerSelection: index});
    } else {
      this.setState({posSelection: index});
    }
    this.updateInstructions()

  }

  updateInstructions() {
    var instructions = ""
    if (this.state.winner != -1) {
      instructions = "Player " + this.state.winner + " Won!"
    }
    else if (this.state.workerSelection == -1) {
      instructions = "Select a worker"
    } else if (this.state.posSelection == -1) {
      instructions = "Select a position"
    } else {
      instructions = "Select a command"
    }
    this.setState({instructions: instructions})
  }

  updateStateFromGame(json: Object) {
    const turn = Number(this.getTurn(json))
    const winner = Number(this.getWinner(json))
    const canMove = this.getCanMove(json)
    const canBuild = this.getCanBuild(json)

    const newCells: Array<Cell> = this.convertToCell(json)

    this.setState({ cells: this.convertToCell(json), turn: turn,
                    playerStates: this.convertPlayerStates(json), winner: winner,
                    canMove: canMove, canBuild: canBuild})

    this.updateInstructions()

    if (!canMove && canBuild) {
      this.setState({workerSelection: this.getWorkerSelection()})
    }

    if (!canMove && !canBuild) {
      this.endTurn()
    }
  }

  getWorkerSelection() {
    var workerSelection = localStorage.getItem( 'workerSelection' ) || "-1";
    return parseInt(workerSelection)
  }

  convertPlayerStates(p: any) : Array<PlayerState> {
    const playerStates: Array<PlayerState> = [];
    for (var i = 0; i < p["playerStates"].length; i++) {
      const workerStates: Array<WorkerState> = [];
      if (p["playerStates"][i]["workerStates"]) {
        for (var j = 0; j < p["playerStates"][i]["workerStates"].length; j++) {
          var w: WorkerState = {
            workerId: p["playerStates"][i]["workerStates"][j]["workerId"],
            x: p["playerStates"][i]["workerStates"][j]["x"],
            y: p["playerStates"][i]["workerStates"][j]["y"]
          }
          workerStates.push(w);
        }
      }

      var godCardString = p["playerStates"][i]["godCard"] ? p["playerStates"][i]["godCard"] : ""
      var c: PlayerState = {
        playerId: p["playerStates"][i]["playerId"],
        workerStates: workerStates,
        godCardString: godCardString,
      };
      playerStates.push(c);
    }
    return playerStates;
  }
  convertToCell(p: any): Array<Cell> {
    const newCells: Array<Cell> = [];

    for (var i = 0; i < p["board"].length; i++) {
      var c: Cell = {
        text: p["board"][i]["text"],
        clazz: `clazz`,
      };
      newCells.push(c);
    }

    return newCells;
  }

  getTurn(p: any): String {
    return p["turn"]
  }

  getWinner(p: any): String | undefined {
    return p["winner"]
  }

  getCanMove(p: any): boolean {
    return p["canMove"]
  }

  getCanBuild(p: any): boolean {
    return p["canBuild"]
  }

  async newGame() {
    const response = await fetch("newgame");
    const json = await response.json();
    this.updateStateFromGame(json);
  }

  async setWorker(url: String) {
    const href = "setworker?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();
    this.updateStateFromGame(json)
    if (json["playerStates"][this.state.turn.valueOf()]["workerStates"].length == 2) {
      this.endTurn()
    }
  }

  async move(url: String) {
    const href = "move?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();
    var index = url.split("=")[url.split("=").length - 1]
    localStorage.setItem( 'workerSelection', index );

    this.updateStateFromGame(json)
  }

  async build(url: String) {
    const href = "build?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();
    this.updateStateFromGame(json)
  }

  async setGodCard(url: String) {
    const href = "setgodcard?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();
    this.endTurn();
    this.updateStateFromGame(json)
  }


  async undo() {
    const response = await fetch("undo");
    const json = await response.json();
    this.updateStateFromGame(json)
  }

  async endTurn() {
    const response = await fetch("endturn");
    const json = await response.json();
    this.updateStateFromGame(json)
  }

  async switch() {
    if (
      window.location.href === "http://localhost:3000/newgame" &&
      oldHref !== window.location.href
    ) {
      console.log("newGame")
      this.newGame();
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/build" &&
      oldHref !== window.location.href
    ) {
      this.build(window.location.href);
      oldHref = window.location.href;
    } else if (
        window.location.href.split("?")[0] === "http://localhost:3000/move" &&
        oldHref !== window.location.href
      ) {
        this.move(window.location.href);
        oldHref = window.location.href;
    } else if (
      window.location.href === "http://localhost:3000/undo" &&
      oldHref !== window.location.href
    ) {
      this.undo();
      oldHref = window.location.href;
    } else if (
      window.location.href === "http://localhost:3000/endturn" &&
      oldHref !== window.location.href
    ) {
      console.log("endTurn")
      this.endTurn();
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/setworker" &&
      oldHref !== window.location.href
    ) {
      console.log("setWorker")
      this.setWorker(window.location.href);
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/setgodcard" &&
      oldHref !== window.location.href
    ) {
      console.log("setgodcard")
      this.setGodCard(window.location.href);
      oldHref = window.location.href;
    }
  };

  tileHasWorker(index : number) : Number {
    var x : number = Math.floor(index/5)
    var y : number = index%5
    for (var player of this.state.playerStates) {
      for (var workerState of player.workerStates) {
        if (workerState.x == x && workerState.y == y) {
          return (workerState.workerId);
        }
      }
    }
    return -1;
  }

  getPlayerFromTile(index : number) : Number {
    var x : number = Math.floor(index/5)
    var y : number = index%5
    for (var player of this.state.playerStates) {
      for (var workerState of player.workerStates) {
        if (workerState.x == x && workerState.y == y) {
          return (player.playerId);
        }
      }
    }
    return -1;
  }

  tileHasWorkerFromPlayer(index : number, playerId : number) : Number {
    var x : number = Math.floor(index/5)
    var y : number = index%5

    if (this.state.playerStates != [] && this.state.playerStates[playerId]) {
      var player = this.state.playerStates[playerId]
      for (var workerState of player.workerStates) {
        if (workerState.x == x && workerState.y == y) {
          return (workerState.workerId);
        }
      }
    }
    return -1;
  }

  resetSelections() {
    this.setState({workerSelection: -1, posSelection: -1});
  }

  generateGodCardScreen() {
    return (
      <div className="App">
        <div style={{textAlign:"center"}}>
          <h1>Santorini</h1>
          <p>It is Player {this.state.turn}'s turn </p>
          <p>Instruction: Choose a god card.</p>
          <br />
        </div>
        <div id="bottombar">
          <a href={"/setgodcard?card=None"}><button>None</button></a>
          <a href={"/setgodcard?card=Demeter"}><button>Demeter</button></a>
          <a href={"/setgodcard?card=Pan"}><button>Pan</button></a>
          <a href={"/setgodcard?card=Minotaur"}><button>Minotaur</button></a>
          <a href={"/newgame"}><button>New Game</button></a>
        </div>
      </div>
    )
  }

  generateSelectWorkerScreen() {
    const rows = [0,0,0,0,0].map((_,i) => { return ( 
      <tr key={"row_" + i}>
        { [...this.state.cells].splice(i*5,5).map(({ text, clazz },j) => { 

          var index = i*5+j;
          var color = "darkorange"
          if (this.state.workerSelection == index) {
            color = "blue"
          } else if (this.state.posSelection == index) {
            color = "green"
          }

          var circleBorder = "0px"
          var circleColor = "white"
          if (this.tileHasWorker(index) != -1) {
            circleBorder = "10px"
            if (this.getPlayerFromTile(index) == 0) {
              circleColor = "black"
            }
          }            
          return (
          <Square
            key={i*5+j}
            onClick={()=>{this.clickHandler(i*5+j)}}
            contents={text}
            color={color}
            circleBorder={circleBorder}
            circleColor={circleColor}
          />
        )}
        )}
      </tr>)
    }
  );

  let playerStatesP : JSX.Element[] = []

  if (this.state.playerStates != [] && this.state.playerStates[0] && 
      this.state.playerStates[0].hasOwnProperty("workerStates")) {
    playerStatesP = this.state.playerStates.map(({playerId, workerStates}) => { return ( 
      <p>Player{playerId}: 
        { workerStates.map(({workerId, x,y}) => <p>workerId {workerId}: ({x},{y})</p>) }
      </p>
    ) }
  );
  }
  const decisionState = "player=" + this.state.turn + "&worker=" + 
    this.tileHasWorker(this.state.workerSelection.valueOf()) + "&pos=" + this.state.posSelection;
  const buildHref = "/build?" + decisionState;

  var godCard = ""

  if (this.state.playerStates.length > 0) {
    godCard = String(this.state.playerStates[this.state.turn.valueOf()].godCardString)
  }

  const workerSelectionScreen = 
    <div className="App">
      <div style={{textAlign:"center"}}>
        <h1>Santorini</h1>
        <p>It is Player {this.state.turn}'s turn </p>
        <p>Player {this.state.turn} has GodCard: {godCard} </p>
        <p>Instruction: Select a worker position</p>
        <p>Position Selection: {this.state.posSelection}</p>
        <div>
          {playerStatesP}
        </div>
        <table cellSpacing="0" id="table" style={{
          margin:'auto',
          width: "auto",
          height:"auto",
          backgroundColor:'darkorange',
          color:'white',
          fontSize:"3em",
          tableLayout:'fixed'
        }}>
          <tbody>
            {rows}
          </tbody>
        </table>
        <br />
      </div>
      <div id="bottombar">
        <a><button onClick={this.resetSelections}>Reset Selection</button></a>
        <a href={"/setworker?" + decisionState} ><button>SetWorker</button></a>
        <a href={"/newgame"}><button>New Game</button></a>
      </div>
    </div>
    return workerSelectionScreen
  }

  generateMainGame() {
    const rows = [0,0,0,0,0].map((_,i) => { return ( 
      <tr key={"row_" + i}>
        { [...this.state.cells].splice(i*5,5).map(({ text, clazz },j) => { 

          var index = i*5+j;
          var color = "darkorange"
          if (this.state.workerSelection == index) {
            color = "blue"
          } else if (this.state.posSelection == index) {
            color = "green"
          }

          var circleBorder = "0px"
          var circleColor = "white"
          if (this.tileHasWorker(index) != -1) {
            circleBorder = "10px"
            if (this.getPlayerFromTile(index) == 0) {
              circleColor = "black"
            }
          }            
          return (
          <Square
            key={i*5+j}
            onClick={()=>{this.clickHandler(i*5+j)}}
            contents={text}
            color={color}
            circleBorder={circleBorder}
            circleColor={circleColor}
          />
        )}
        )}
      </tr>)
    }
  );

  let playerStatesP : JSX.Element[] = []

  if (this.state.playerStates != [] && this.state.playerStates[0] && 
      this.state.playerStates[0].hasOwnProperty("workerStates")) {
    playerStatesP = this.state.playerStates.map(({playerId, workerStates}) => { return ( 
      <p>Player{playerId}: 
        { workerStates.map(({workerId, x,y}) => <p>workerId {workerId}: ({x},{y})</p>) }
      </p>
    ) }
  );
  }
  const decisionState = "player=" + this.state.turn + "&worker=" + 
    this.tileHasWorker(this.state.workerSelection.valueOf()) + "&pos=" + this.state.posSelection;
  const buildHref = "/build?" + decisionState;

  var godCard = ""

  if (this.state.playerStates.length > 0) {
    godCard = String(this.state.playerStates[this.state.turn.valueOf()].godCardString)
  }

  var addLevelButton = undefined
  var addDomeButton = undefined
  var moveButton = undefined

  if (this.state.canMove) {
    moveButton = <a href={"/move?" + decisionState}><button>Move</button></a>
  }
  else {
    addDomeButton = <a href={"/build?" + decisionState} ><button>Build Dome</button></a>
    addLevelButton = <a href={"/build?" + decisionState} ><button>Build Level</button></a>
  }





  const mainGame = 
    <div className="App">
      <div style={{textAlign:"center"}}>
        <h1>Santorini</h1>
        <p>It is Player {this.state.turn}'s turn </p>
        <p>Player {this.state.turn} has GodCard: {godCard} </p>
        <p>Instruction: {this.state.instructions}</p>
        <p>Worker Selection: {this.state.workerSelection}</p>
        <p>Position Selection: {this.state.posSelection}</p>
        <p>Player can Move? {Boolean(this.state.canMove).toString()}</p>
        <p>Player can Build? {Boolean(this.state.canBuild).toString()}</p>
        <table cellSpacing="0" id="table" style={{
          margin:'auto',
          width: "auto",
          height:"auto",
          backgroundColor:'darkorange',
          color:'white',
          fontSize:"3em",
          tableLayout:'fixed'
        }}>
          <tbody>
            {rows}
          </tbody>
        </table>
        <br />
      </div>
      <div id="bottombar">
        <a><button onClick={this.resetSelections}>Reset Selections</button></a>
        {addLevelButton}
        {addDomeButton}
        {moveButton}
        <a href={"/endturn"}><button>End Turn Early</button></a>
        <a href={"/newgame"}><button>New Game</button></a>
      </div>
    </div>

    const wonGame = 
    <div className="App">
      <div style={{textAlign:"center"}}>
        <h1>Santorini</h1>
        <p>It is Player {this.state.turn}'s turn </p>
        <p>Player {this.state.turn} has GodCard: {godCard} </p>
        <p>Instruction: {this.state.instructions}</p>
        <table cellSpacing="0" id="table" style={{
          margin:'auto',
          width: "auto",
          height:"auto",
          backgroundColor:'darkorange',
          color:'white',
          fontSize:"3em",
          tableLayout:'fixed'
        }}>
          <tbody>
            {rows}
          </tbody>
        </table>
        <br />
      </div>
      <div id="bottombar">
        <a href={"/newgame"}><button>New Game</button></a>
      </div>
    </div>

    if (this.state.winner != -1) {
      return wonGame
    }

    return mainGame
  }

  render() {
    this.switch()

    if (this.state.playerStates.length > 0 && 
      (this.state.playerStates[0].godCardString == "" || this.state.playerStates[1].godCardString == "")) {
      return this.generateGodCardScreen();
    } else if (this.state.playerStates.length > 0 && 
      ((this.state.playerStates[0].workerStates == undefined || this.state.playerStates[0].workerStates.length < 2) ||
      (this.state.playerStates[1].workerStates == undefined || this.state.playerStates[1].workerStates.length < 2))) {
      return this.generateSelectWorkerScreen();
    }

    return this.generateMainGame()
  };
};

export default App;
