import Handlebars from "handlebars"
import { Component } from 'react'
import './App.css'
import { Square } from './Square'

var oldHref = "http://localhost:3000"

interface Cell {
  text: String;
  clazz: String;
}

interface Cells {
  cells: Array<Cell>,
  instructions: String,
  posSelection: Number
  turn: Number
  workerSelection: Number
}

interface Props {
}

class App extends Component<Props, Cells> {
  constructor(props: Props) {
    super(props);
    this.state = {
      cells: [ 
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
      ],
      instructions: "It is Player 2's turn",
      posSelection: -1,
      workerSelection: -1,
      turn: 0,
    };
    this.clickHandler = this.clickHandler.bind(this);
  }

  clickHandler(index: Number){
    console.log(index)
    if(this.state.workerSelection == -1) {
      this.setState({workerSelection: index});
    } else {
      this.setState({posSelection: index});
    }
  }


  convertToCell(p: any): Array<Cell> {
    const newCells: Array<Cell> = [];

    for (var i = 0; i < p["board"].length; i++) {
      var c: Cell = {
        text: p["board"][i]["text"],
        //clazz: p["board"][i]["clazz"],
        clazz: `clazz`,
        link: p["board"][i]["link"],
      };
      newCells.push(c);
    }

    return newCells;
  }

  getTurn(p: any): String {
    return p["turn"]
  }

  getWinner(p: any): String | undefined {
    return p["winner"]
  }

  getInstr(turn: String, winner: String | undefined) {
    if (winner === undefined) return "It is Player " + turn + "'s turn"
    else return "Player " + winner + " wins!"
  }

  async newGame() {

    const response = await fetch("newgame");
    const json = await response.json();
    console.log(json)
    const turn = Number(this.getTurn(json))

    const newCells: Array<Cell> = this.convertToCell(json);
    this.setState({ cells: this.convertToCell(json), instructions: "It is Player 0's turn", turn: turn})
  }

  async setWorker(url: String) {
    const href = "setworker?" + url.split("?")[1];
    console.log("hi")
    const response = await fetch(href);
    const json = await response.json();
    console.log(json)

    const newCells: Array<Cell> = this.convertToCell(json);
    const turn = this.getTurn(json)
    console.log(turn);
    const winner = this.getWinner(json)
    const instr = this.getInstr(turn, winner)
    this.setState({ cells: newCells, instructions: instr })
  }

  async play(url: String) {
    const href = "play?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();

    const newCells: Array<Cell> = this.convertToCell(json);
    const turn = this.getTurn(json)
    console.log(turn);
    const winner = this.getWinner(json)
    const instr = this.getInstr(turn, winner)
    this.setState({ cells: newCells, instructions: instr })
  }

  async undo() {
    const response = await fetch("undo");
    const json = await response.json();

    const newCells: Array<Cell> = this.convertToCell(json);
    const turn = this.getTurn(json)
    const winner = this.getWinner(json)
    const instr = this.getInstr(turn, winner)
    this.setState({ cells: newCells, instructions: instr })
  }

  async endTurn() {
    const response = await fetch("endturn");
    const json = await response.json();
    console.log(json)
    const newCells: Array<Cell> = this.convertToCell(json);
    const turn = this.getTurn(json)
    const winner = this.getWinner(json)
    const instr = this.getInstr(turn, winner)
    this.setState({ cells: newCells, instructions: instr, turn: Number(turn)})
  }

  async switch() {
    if (
      window.location.href === "http://localhost:3000/newgame" &&
      oldHref !== window.location.href
    ) {
      console.log("newGame")
      this.newGame();
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/play" &&
      oldHref !== window.location.href
    ) {
      this.play(window.location.href);
      oldHref = window.location.href;
    } else if (
      window.location.href === "http://localhost:3000/undo" &&
      oldHref !== window.location.href
    ) {
      this.undo();
      oldHref = window.location.href;
    } else if (
      window.location.href === "http://localhost:3000/endturn" &&
      oldHref !== window.location.href
    ) {
      console.log("endTurn")
      this.endTurn();
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/setworker" &&
      oldHref !== window.location.href
    ) {
      console.log("setWorker")
      this.setWorker(window.location.href);
      oldHref = window.location.href;
    }
  };

  render() {
    this.switch()

    const style = {
      margin:'auto',
      width: "auto",
      height:"auto",
      backgroundColor:'darkorange',
      color:'white',
      fontSize:"3em",
      tableLayout:'fixed',
    }

    const rows = [0,0,0,0,0].map((_,i) => { return ( 
        <tr key={"row_" + i}>
          { [...this.state.cells].splice(i*5,5).map(({ text, clazz, link },j) => { return (
            <Square
              key={i*5+j}
              onClick={()=>{this.clickHandler(i*5+j)}}
           />
          )}
          )}
        </tr>)
      }
    );

    const decisionState = "player=" + this.state.turn + "&worker=" + this.state.workerSelection + "&pos=" + this.state.posSelection;
    const buildHref = "/build?" + decisionState;

    return (
      <div className="App">
          <div style={{textAlign:"center"}}>
            <h1>Santorini</h1>
            <p>{this.state.instructions}</p>
            <p>{this.state.workerSelection}</p>
            <p>{this.state.posSelection}</p>
            <table cellSpacing="0" id="table" style={{
              margin:'auto',
              width: "auto",
              height:"auto",
              backgroundColor:'darkorange',
              color:'white',
              fontSize:"3em",
              tableLayout:'fixed'
            }}>
              <tbody>
                {rows}
              </tbody>
            </table>
            <br />
          </div>
          <div id="bottombar">
            <a href={"/setworker?" + decisionState} ><button>SetWorker</button></a>
            <a href={"/build?" + decisionState} ><button>Build</button></a>
            <a href={"/move?" + decisionState}><button>Move</button></a>
            <a href={"/endturn"}><button>End Turn</button></a>
            <a href={"/newgame"}><button>New Game</button></a>
          </div>
      </div>
    );
  };
};

export default App;
