import { Component } from 'react'
import './App.css'
import { Square } from './Square'

var oldHref = "http://localhost:3000"

interface Cell {
  text: String;
  clazz: String;
}

interface WorkerState {
  workerId: Number;
  x: Number;
  y: Number;
}

interface PlayerState {
  playerId: Number;
  workerStates: Array<WorkerState>;
}

interface Cells {
  cells: Array<Cell>,
  instructions: String,
  playerStates: Array<PlayerState>
  workerSelection: Number
  posSelection: Number
  turn: Number

}

interface Props {
}

class App extends Component<Props, Cells> {
  constructor(props: Props) {
    super(props);
    this.state = {
      cells: [ 
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"},
        { text: "", clazz: "playable"}, 
      ],
      instructions: "",
      posSelection: -1,
      workerSelection: -1,
      turn: 0,
      playerStates: [],
    };
    this.clickHandler = this.clickHandler.bind(this);
    this.resetSelections = this.resetSelections.bind(this);
  }


  clickHandler(index: number){
    console.log(index)
    if(this.tileHasWorkerFromPlayer(index, this.state.turn.valueOf()) != -1) {
      this.setState({workerSelection: index});
    } else {
      this.setState({posSelection: index});
    }

    this.updateInstructions()

  }

  updateInstructions() {
    var instructions = ""
    if(this.state.workerSelection == -1) {
      instructions = "Select a worker"
    } else if (this.state.posSelection == -1) {
      instructions = "Select a position"
    } else {
      instructions = "Select a command"
    }
    this.setState({instructions: instructions})
  }

  updateStateFromGame(json: Object) {
    const turn = Number(this.getTurn(json))

    const newCells: Array<Cell> = this.convertToCell(json)
    this.updateInstructions()

    this.setState({ cells: this.convertToCell(json), turn: turn,
                    playerStates: this.convertPlayerStates(json)})
  }

  convertPlayerStates(p: any) : Array<PlayerState> {
    if (p["playerStates"] == null) {
      return [];
    }
    const playerStates: Array<PlayerState> = [];
    for (var i = 0; i < p["playerStates"].length; i++) {
      const workerStates: Array<WorkerState> = [];
      for (var j = 0; j < p["playerStates"][i]["workerStates"].length; j++) {
        var w: WorkerState = {
          workerId: p["playerStates"][i]["workerStates"][j]["workerId"],
          x: p["playerStates"][i]["workerStates"][j]["x"],
          y: p["playerStates"][i]["workerStates"][j]["y"]
        }
        workerStates.push(w);
      }
      var c: PlayerState = {
        playerId: p["playerStates"][i]["playerId"],
        workerStates: workerStates,
      };
      playerStates.push(c);
    }
    return playerStates;
  }
  convertToCell(p: any): Array<Cell> {
    const newCells: Array<Cell> = [];

    for (var i = 0; i < p["board"].length; i++) {
      var c: Cell = {
        text: p["board"][i]["text"],
        clazz: `clazz`,
      };
      newCells.push(c);
    }

    return newCells;
  }

  getTurn(p: any): String {
    return p["turn"]
  }

  getWinner(p: any): String | undefined {
    return p["winner"]
  }

  getInstr(turn: String, winner: String | undefined) {
    if (winner === undefined) return "It is Player " + turn + "'s turn"
    else return "Player " + winner + " wins!"
  }

  async newGame() {
    const response = await fetch("newgame");
    const json = await response.json();
    this.updateStateFromGame(json);
  }

  async setWorker(url: String) {
    const href = "setworker?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();
    this.updateStateFromGame(json)
  }

  async move(url: String) {
    const href = "move?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();
    this.updateStateFromGame(json)
  }

  async build(url: String) {
    const href = "build?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();
    this.updateStateFromGame(json)
  }


  async undo() {
    const response = await fetch("undo");
    const json = await response.json();
    this.updateStateFromGame(json)
  }

  async endTurn() {
    const response = await fetch("endturn");
    const json = await response.json();
    console.log(json)
    this.updateStateFromGame(json)
  }

  async switch() {
    if (
      window.location.href === "http://localhost:3000/newgame" &&
      oldHref !== window.location.href
    ) {
      console.log("newGame")
      this.newGame();
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/build" &&
      oldHref !== window.location.href
    ) {
      this.build(window.location.href);
      oldHref = window.location.href;
    } else if (
        window.location.href.split("?")[0] === "http://localhost:3000/move" &&
        oldHref !== window.location.href
      ) {
        this.move(window.location.href);
        oldHref = window.location.href;
    } else if (
      window.location.href === "http://localhost:3000/undo" &&
      oldHref !== window.location.href
    ) {
      this.undo();
      oldHref = window.location.href;
    } else if (
      window.location.href === "http://localhost:3000/endturn" &&
      oldHref !== window.location.href
    ) {
      console.log("endTurn")
      this.endTurn();
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/setworker" &&
      oldHref !== window.location.href
    ) {

      console.log("setWorker")
      this.setWorker(window.location.href);
      oldHref = window.location.href;
    }
  };

  tileHasWorker(index : number) : Number {
    var x : number = Math.floor(index/5)
    var y : number = index%5
    for (var player of this.state.playerStates) {
      for (var workerState of player.workerStates) {
        if (workerState.x == x && workerState.y == y) {
          return (workerState.workerId);
        }
      }
    }
    return -1;
  }

  tileHasWorkerFromPlayer(index : number, playerId : number) : Number {
    var x : number = Math.floor(index/5)
    var y : number = index%5
    var player = this.state.playerStates[playerId]
    for (var workerState of player.workerStates) {
      if (workerState.x == x && workerState.y == y) {
        return (workerState.workerId);
      }
    }
    return -1;
  }

  resetSelections() {
    this.setState({workerSelection: -1, posSelection: -1});
  }

  render() {
    this.switch()

    const style = {
      margin:'auto',
      width: "auto",
      height:"auto",
      backgroundColor:'darkorange',
      color:'white',
      fontSize:"3em",
      tableLayout:'fixed',
    }

    const rows = [0,0,0,0,0].map((_,i) => { return ( 
        <tr key={"row_" + i}>
          { [...this.state.cells].splice(i*5,5).map(({ text, clazz },j) => { 
            var color = "darkorange"
            if (this.state.workerSelection == i*5+j) {
              color = "blue"
            } else if (this.state.posSelection == i*5+j) {
              color = "green"
            }
            return (
            <Square
              key={i*5+j}
              onClick={()=>{this.clickHandler(i*5+j)}}
              contents={text}
              color={color}
            />
          )}
          )}
        </tr>)
      }
    );

    let playerStatesP : JSX.Element[] = []

    if (this.state.playerStates != [] && this.state.playerStates[0] && this.state.playerStates[0].hasOwnProperty("workerStates")) {
      playerStatesP = this.state.playerStates.map(({playerId, workerStates}) => { return ( 
        <p>Player{playerId}: 
          { workerStates.map(({workerId, x,y}) => <p>workerId {workerId}: ({x},{y})</p>) }
        </p>
      ) }
    );
    }
